const axios = require('axios');

const exportDataSource = async () => {
  try {
    const response = await axios.post(
      'https://apiweb.mile.app/api/v3/data/export',
      null,
      {
        params: {
          download: 'xlsx',
          dataTypeId: '6422816ef6710f053056d812',
          fields: 'dataId,name,address',
          hubId: '63c63ab1b6972d3150348172',
          limit: 100,
          sortBy: 'createdTime',
          sortOrder: 'desc'
        },
        headers: {
          'Authorization': 'Bearer YOUR_ACCESS_TOKEN'
        }
      }
    );

    console.log('Export completed:', response.data);
    console.log('Download link:', response.data.data.link);
  } catch (error) {
    console.error('Export failed:', error.response?.data || error.message);
  }
};

exportDataSource();

const axios = require('axios');

const exportTasks = async () => {
  try {
    const response = await axios.post(
      'https://apiweb.mile.app/api/v3/tasks/export',
      {
        download: 'xlsx',
        fields: 'startTime,endTime,hubId,createdBy,flow,status',
        status: 'DONE',
        flowId: '63d8c9b0dbd5ef20a627dd86',
        hubId: '621dd813eb3ebf16b94d6969',
        page: 1,
        limit: 100,
        sortBy: 'createdTime',
        sortOrder: 'desc',
        background: false
      },
      {
        headers: {
          'Authorization': 'Bearer YOUR_ACCESS_TOKEN',
          'Content-Type': 'application/json'
        }
      }
    );

    console.log('Export completed:', response.data);
    console.log('Download link:', response.data.data.link);
  } catch (error) {
    console.error('Export failed:', error.response?.data || error.message);
  }
};

exportTasks();

const axios = require('axios');

const getExportLog = async () => {
  try {
    const response = await axios.get(
      'https://apiweb.mile.app/api/v3/tasks/export-log',
      {
        params: {
          page: 1,
          limit: 10,
          sortBy: 'requestAt',
          sortOrder: 'desc',
          status: 'Done',
          dataType: 'task'
        },
        headers: {
          'Authorization': 'Bearer YOUR_ACCESS_TOKEN'
        }
      }
    );

    console.log('Total exports:', response.data.total);
    response.data.data.forEach(log => {
      const source = log._source;
      console.log(`Export: ${source.dataName}`);
      console.log(`Status: ${source.status}`);
      console.log(`Download: ${source.fileUrl}`);
      console.log('---');
    });
  } catch (error) {
    console.error('Failed to fetch export log:', error.response?.data || error.message);
  }
};

getExportLog();
